// / Summary
// Finds the first floor that goes negative
func findFloor(s string) {
	var floor int = 0
	for i, x := range s {
		switch x {
		case '(':
			floor++
		case ')':
			floor--
		}
		if floor < 0 {
			fmt.Println(i + 1)
			break
		}
	}
}
